import React, { useState, useEffect  } from 'react';
import { useAsync } from "react-async-hook";

var source;
const ImgurComponent = () => {  
    const [img, setImg] = useState(null);

    useAsync(async () => {
        var chars = '01234567890ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghiklmnopqrstuvwxyz'; 
        var stringlength = 5; /* could be 6 or 7, but takes forever because there are lots of dead images */
        var text = '';
        for (var i = 0; i < stringlength; i++) {
          var rnum = Math.floor(Math.random() * chars.length);
          text += chars.substring(rnum,rnum+1);
        }

        // var image = new Image();
        // image.onload = function() {
        //   if (this.width == 161) {
        //     randomString();
        //   } else {
        //     $('.stuff img').replaceWith(this);
        //     $('#output').text(text);
        //   }
        // };
        // image.src = source;
        source = 'https://i.imgur.com/' + text + '.jpg';
        setImg(source);
    }, []);

    return (
        <div key={img}>
            <img className="imgurimg" src={img}></img>
        </div>
    );
};

export default ImgurComponent;
