import "./QuestionButton.css"

const [isToggled, setIsToggled] = useState(false);

const handleChange = () => {
    setIsToggled(!isToggled);
  };

export default function GlossaryButton(props) {
    return (
        <>
        <button onClick={handleChange}>
            {props.subject}
        </button>
        <button>
            {/* {props.terms} */}
            {isToggled ? `${props.terms}` : 'OFF'}
        </button>
        </>
    )
}
