          {answers.map((option) => {
            return (
              <QuestionButton label={option} onAttempt={() => onAttempt(option)}/>
            )
          })}
          </div>
        </div> */}
        <div>
          <div className="glossary"> 
          {glossary.subject.map((item) => {
            return (
              <QuestionButton label={item}/>
            )
          })}
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
