import './App.css';
import { useState } from 'react';
import GlossaryArray from './model/GlossaryArray'
import QuestionButton from './components/QuestionButton'

function App() {
  const [message, setMessage] = useState()
  const [currentQuestion, setCurrentQuestion] = useState(0)
  const [glossary, setGlossary] = useState(GlossaryArray)
  const [clickedQuestions, setClickedQuestions] = useState([])

  function nextQuestion() {
    // setMessage("")
    const nextIndex = currentQuestion >= glossary.length - 1 ? 0 : currentQuestion + 1;
    setCurrentQuestion(nextIndex);
    setClickedQuestions([...clickedQuestions, currentQuestion]);
  }


  return (
    <div className="App">
      <header className="App-header">
        <div className="quiz">
          <h1>Quiz time!</h1>
            <div>What is {glossary[currentQuestion].question}?</div>
            <h3>{message}</h3>
            <div className="answers"> 
            {glossary[currentQuestion].shuffleOptions().map((option, index) => {
              return (
                <QuestionButton key={index} label={option}/>
              )
            })}
            </div>
            <button onClick={() => nextQuestion()}>Click me</button>
            <div>
              <h2>Clicked Questions:</h2>
              <ul>
                {clickedQuestions.map((questionIndex, index) => (
                  <li key={index}>{glossary[questionIndex].question}</li>
                ))}
              </ul>
            </div>
        </div>
      </header>
    </div>
  );
}

export default App;
