import './App.css';
import { React, useState, useEffect } from 'react';

function App() {
  const [currentData, setCurrentData] = useState(0)
  const [latestNews, setLatestNews] = useState([]);
  const [clickedData, setClickedData] = useState([])


  useEffect(() => {
    const url = 'https://api.currentsapi.services/v1/latest-news?' +
      'language=us&' +
      'apiKey=xxHFtZK5Dip9LgWR7F21hsUJk1gwMkbrPX5rtEtF9opHFRdn';
    const fetchLatestNews = async () => {
      try {
        const response = await fetch(url);
        if (!response.ok) {
          throw new Error('Failed to fetch latest news');
        }
        const data = await response.json();
        setLatestNews(data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchLatestNews();
  }, []);

    function nextQuestion() {
      const nextIndex = currentData >= latestNews.length - 1 ? 0 : currentData + 1;
      setCurrentData(nextIndex);
      setClickedData([...clickedData, currentData]);
    }

  return (
    <div className="App">
      {/* <div className="quiz">
