              {latestNews[currentData] && (
                <div key={latestNews[currentData].publishedAt}>
                  {latestNews[currentData].title}
                </div>
              )}
              {clickedData.length > 0 && clickedData.map((questionIndex, index) => (
                <div key={index}>
                  {latestNews[questionIndex] && latestNews[questionIndex].title}
                </div>
              ))}
            </div>
        </div> */}
        <h1>Latest News</h1>
      <ul>
        {latestNews.map((article, index) => (
          <li key={index}>
            <h2>{article.title}</h2>
            <p>{article.description}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
