import './App.css';
import { useState } from 'react';
// import Question from './model/Questions'
import Glossary from './model/Glossary'
import QuestionButton from './components/QuestionButton'

function App() {
  const [message, setMessage] = useState()
  const [glossary, setGlossary] = useState([
    new Glossary(0, "React", "JavaScript framework that works with JSX files.", ["Answer one", "two", "three"], false),
    new Glossary(1, "JSX", "HTML + JavaScript. Server-side scripting. Functions can be returned in pure HTML.", ["Answer one", "two", "three"], false),
    new Glossary(2, "XML", "Extensible Markup Language. XML was designed to carry data - with focus on what data is. Does not display data.", ["Answer one", "two", "three"], false),
    new Glossary(3, "To start a project...", "npx create-react-app [name of project]", ["Answer one", "two", "three"], false),
    new Glossary(4, "Component", "Reusable functions. React only re-renders components that have changed. State is like a variable.", ["Answer one", "two", "three"], false),
    new Glossary(5, "useState", "Re-renders once the function has been manipulated. It’s a React hook - functional compenet that tracks its own data. It takes a reference to the value (variable - but don't refer to this variable in the function), and then a function name. UseState will start at whatever is in its parameter. UseState is how we use a function to a component instead of a class. E.g.: const [count, setCount] = useState(0). A state variable should always be declared at the highest level its needed.", ["Answer one", "two", "three"], false),
    new Glossary(6, "Hooks", "functional components as opposed to class components. Functional component is a reusable, imported. A function that returns some HTML.", ["Answer one", "two", "three"], false),
    new Glossary(7, "Props", "A generic parameter passed via the function. Object full of properties that we can assign. For example, you can give props to a component and assign them in the component name in app.js.", ["Answer one", "two", "three"], false),
    new Glossary(8, "OnChange", "This will always give you an event object. Every onChange passes an event object, containing info about the event.", ["Answer one", "two", "three"], false),
    new Glossary(9, "Callback function", "Parent (app.js) passes a State function as props to a child element (colourbutton.js). The child gets State of parent. This function or state can be a prop. Steps for making a prop: 1. Make a custom prop in your app.js component name i.e. <ColourButton col={'green'}/>. 2. Bring prop as an attribute in the component function name i.e. ColourButton(props). 3. Use it inside your component i.e. style={{backgroundColor: props.col}}. All inline!", ["Answer one", "two", "three"], false),
    new Glossary(10, "Toggle", "Make a useState(false). Underneath make a toggleClass function (i.e. const toggleClass = () => {setBackground(!background)}). In the button inline make a ternary operator (i.e. style={{ backgroundColor: background ? 'darkslateblue' : 'green' }} ).", ["Answer one", "two", "three"], false),
    new Glossary(11, "Map", "Like a For loop. Applies a function to an array and returns a new array. Make sure to always put a key right inside the return statement when calling the component.", ["Answer one", "two", "three"], false),
    new Glossary(12, "Flex", "Flex will always affect the child elements. Full flex is 1. 50% will be 0.5.", ["Answer one", "two", "three"], false),
  ])

  const toggleVisibility = (id) => {
    setGlossary(glossary.map(item =>
      item.id === id ? { ...item, isVisible: !item.isVisible } : item
    ));
  };

  // for Quiz:

  const [currentQuestion, setCurrentQuestion] = useState("")

  function nextQuestion() {
    currentQuestion >= glossary.length - 1 ?
      setCurrentQuestion(0) : setCurrentQuestion(currentQuestion + 1)
  }

  function onAttempt(attempt) {
    if (glossary[currentQuestion].checkAnswer(attempt)) {
      setMessage("Correct!! :)")
      setTimeout(() => nextQuestion(), 1500);
    } else {
      setMessage("Incorrect.. :(")
    }
  }

  const answers = glossary[currentQuestion].shuffleOptions()

  return (
    <div className="App">
      <header className="App-header">
        <div className="glossary-container">
          {glossary.map(item => (
            <div className="glossary">
            <button key={item.id} className="subject" onClick={() => toggleVisibility(item.id)}>
              {item.question}</button>
            <button className="terms" style={{ display: item.isVisible ? 'block' : 'none' }}>{item.correctAnswer}</button>
            </div>
          ))}
        </div>
        <div className="quiz">
          <h1>Quiz!</h1>
            <div>What is{glossary[currentQuestion].question}?</div>
            <h3>{message}</h3>
            <div className="answers"> 
            {answers.map((option) => {
              return (
                <QuestionButton label={option} onAttempt={() => onAttempt(option)}/>
              )
            })}
            </div>
        </div>
      </header>
    </div>
  );
}

export default App;
