import './App.css';
import { React, useState, useEffect } from 'react';

function App() {
  const [currentData, setCurrentData] = useState(0)
  const [data, setData] = useState([]);
  const [clickedData, setClickedData] = useState([])


  const apikey = 'apiKey=xxHFtZK5Dip9LgWR7F21hsUJk1gwMkbrPX5rtEtF9opHFRdn';
    const url = 'https://api.currentsapi.services/v1/latest-news?' + 'language=us&' + apikey;
    useEffect(() => {
     fetch(url).then(result => result.json()).then(alldata => setData(alldata.news)).catch(error => console.log(error.message))
    }, [])

    function nextQuestion() {
      const nextIndex = currentData >= data.length - 1 ? 0 : currentData + 1;
      setCurrentData(nextIndex);
      setClickedData([...clickedData, currentData]);
    }

  return (
    <div className="App">
      <div className="quiz">
          <button onClick={() => nextQuestion()}>Add</button>
            <div className="answers"> 
              {data[currentData] && (
                <div key={data[currentData].publishedAt}>
                  {data[currentData].title}
                </div>
              )}
              {clickedData.length > 0 && clickedData.map((questionIndex, index) => (
                <div key={index}>
                  {data[questionIndex] && data[questionIndex].title}
                </div>
              ))}
            </div>
        </div>
    </div>
  );
}

export default App;
