import './App.css';
import { React, useState, useEffect } from 'react';
import { GiphyFetch } from "@giphy/js-fetch-api";
import { Gif } from "@giphy/react-components";
import { useAsync } from "react-async-hook";

function App() {
  const [currentQuestion, setCurrentQuestion] = useState(0)
  const [glossary, setGlossary] = useState([]);
  const [clickedQuestions, setClickedQuestions] = useState([])

  const apikey = '48878147a05368955e614dca472e6dc4';
    const category = 'world';
    const url = 'https://gnews.io/api/v4/top-headlines?category=' + category + '&lang=en&country=us&max=50&apikey=' + apikey;
    useEffect(() => {
     fetch(url).then(result => result.json()).then(alldata => setGlossary(alldata.articles)).catch(error => console.log(error.message))
    }, [])

    function nextQuestion() {
      const nextIndex = currentQuestion >= glossary.length - 1 ? 0 : currentQuestion + 1;
      setCurrentQuestion(nextIndex);
      setClickedQuestions([...clickedQuestions, currentQuestion]);
    }

    const giphyFetch = new GiphyFetch("sXpGFDGZs0Dv1mmNFvYaGUvYwKX0PWIh");

    function GifDemo() {
      const [gif, setGif] = useState(null);
      useAsync(async () => {
        const { data } = await giphyFetch.gif("fpXxIjftmkk9y");
        setGif(data);
      }, []);
      return gif && <Gif gif={gif} width={200} />;
    }

  return (
    <div className="App">
      <div className="quiz">
          <button onClick={() => nextQuestion()}>Add</button>
            <div className="answers"> 
              {glossary[currentQuestion] && (
                <div key={glossary[currentQuestion].publishedAt}>
                  {glossary[currentQuestion].title}
                </div>
              )}
              <GifDemo />
              {clickedQuestions.map((questionIndex, index) => (
                <div key={index}>
                  {glossary[questionIndex].title}
                </div>
              ))}
            </div>
        </div>
    </div>
  );
}

export default App;
