import "./QuestionButton.css"

export default function GlossaryButton(items, toggle) {
    return (
        <div className="glossary">
            {items.map((item, index) => {
            return (
              <>
              <button onClick={() => items.action(item.terms)}>
                {item.subject}
                </button>
                <button>
                {toggle ? `${item.terms}` : ""}
                </button>
              </>
            )
          })}
        </div>
    )
}

